userDefinedLinkHintCss:
:root {
    --vimiium-font-size: 14;
    --vimiium-font-size-hints: 12;
    --vimiium-font-size-url: 12;

    --vimiium-font-weight: normal;
    --vimiium-font-weight-medium: medium;
    --vimiium-font-weight-bold: bold;

    --vimiium-font: "Helvetica Neue", "Helvetica", "Arial", sans-serif;
    /* Font used in the UI */

    --vimiium-padding: 2px;
    --vimiium-padding-vomnibar: 6px;
    --vimiium-shadow: 0 2px 4px rgba(0, 0, 0, 0.12), 0 1px 2px rgba(0, 0, 0, 0.24);

    /* ------------ VOMNIBAR & HUD ------------ */
    --vimiium-fg: #FFFFFF;
    --vimiium-fg-dark: #ffffff65;
    --vimiium-fg-url: #84BFFA;
    --vimiium-fg-match: #d4e7ff;
    /* ALTERNATIVE `--vimiium-fg-match' COLOR: 
        --vimiium-fg-match: #F29A4A; */

    --vimiium-bg: #2A2A2E;
    --vimiium-bg-searcharea: #222225;
    --vimiium-bg-selected: #2165D9;

    --vimiium-border: #404046;

    /* ------------ HINTS ------------ */
    --vimiium-bg-hints: #FFD752;
    /* Background color of link hints */
    --vimiium-border-hints: #F4CA46;
    /* Border color of link hints */
    --vimiium-fg-hints: #000000;
    /* Text color of link hints, (don't forget to change `#vimiumHintMarkerContainer div > .matchingCharacter ~ span`) */
}

/* ------------------------------------------------- CSS ------------------------------------------------- */

/* ------------ HINTS ------------ */
#vimiumHintMarkerContainer div.internalVimiumHintMarker,
#vimiumHintMarkerContainer div.vimiumHintMarker {
    background: var(--vimiium-bg-hints);
    border: 1px solid var(--vimiium-border-hints);
    box-shadow: var(--vimiium-shadow);
    padding: 2px 3px;
}

#vimiumHintMarkerContainer div span {
    color: var(--vimiium-fg-hints);
    font-family: var(--vimiium-font);
    font-size: var(--vimiium-font-size-hints);
    font-weight: var(--vimiium-font-weight-bold);
    text-shadow: none;
}

#vimiumHintMarkerContainer div>.matchingCharacter {
    opacity: 0.3;
}

#vimiumHintMarkerContainer div>.matchingCharacter~span {
    color: var(--vimiium-hints-main-fg);
}

/* ------------ VOMNIBAR ------------ */
#vomnibar {
    animation: show 200ms cubic-bezier(0, 0, 0.2, 1) forwards;
    background: var(--vimiium-bg);
    border: 1px solid var(--vimiium-border);
    box-shadow: var(--vimiium-shadow);
    border-radius: 5px;
}

/* Animate Slide in */
@keyframes show {
    0% {
        opacity: 0;
        transform: translateY(50px);
    }

    100% {
        opacity: 1;
        transform: translateY(0);
    }
}

#vomnibar .vomnibarSearchArea,
#vomnibar input {
    /* ALTERNATE BACKGROUND COLOR:
        background: transparent; */
    background: var(--vimiium-bg-searcharea);
    border: none;
    box-shadow: none;
    color: var(--vimiium-fg);
    font-family: var(--vimiium-font);
    font-size: var(--vimiium-font-size);
    font-weight: var(--vimiium-font-weight);
}

#vomnibar .vomnibarSearchArea {
    padding: 8px 30px;
    border-radius: 6px;
}

#vomnibar input {
    border: none;
    padding: var(--vimiium-padding);
}

#vomnibar ul {
    background: var(--vimiium-bg);
    border-top: 1px solid var(--vimiium-border);
    margin: 0;
    padding: var(--vimiium-padding-vomnibar);
}

#vomnibar li {
    border-bottom: none;
    padding-top: 8px;
    padding-left: var(--vimiium-padding-vomnibar);
    padding-right: var(--vimiium-padding-vomnibar);
    padding-bottom: 8px;
}

#vomnibar li .vomnibarTopHalf {
    padding: var(--vimiium-padding);
}

#vomnibar li .vomnibarBottomHalf {
    padding-top: 0px;
    padding-bottom: 1px;
}

#vomnibar li .vomnibarSource {
    color: var(--vimiium-fg-dark);
    font-family: var(--vimiium-font);
    font-size: 12;
    font-weight: var(--vimiium-font-weight-bold);
    text-transform: uppercase;
    /* `.vomnibarSource` will be in all uppercase letters. Delete to revert to original */
}

#vomnibar li em,
#vomnibar li .vomnibarTitle {
    color: var(--vimiium-fg);
    font-family: var(--vimiium-font);
    font-size: var(--vimiium-font-size);
    font-weight: var(--vimiium-font-weight-medium);
}

#vomnibar li .vomnibarUrl {
    color: var(--vimiium-fg-url);
    font-family: var(--vimiium-font);
    font-size: var(--vimiium-font-size-url);
    font-weight: var(--vimiium-font-weight-medium);
}

#vomnibar li .vomnibarMatch {
    color: var(--vimiium-fg-match);
    font-weight: normal;
}

#vomnibar li .vomnibarTitle .vomnibarMatch {
    color: var(--vimiium-fg);
    font-weight: bold;
}

#vomnibar li.vomnibarSelected {
    background-color: var(--vimiium-bg-selected);
    border-radius: 3px;
}

#vomnibar li.vomnibarSelected .vomnibarUrl {
    /* Changes '.vomnibarURL' to '--vimiium-fg-match' when selected */
    color: var(--vimiium-fg-match);
}

#vomnibar li.vomnibarSelected .vomnibarMatch {
    /* Changes '.vomnibarMatch' to '--vimiium-fg' when selected */
    color: var(--vimiium-fg);
}

/* ------------ HUD ------------ */
div.vimiumHUD {
    background: var(--vimiium-bg);
    border: 1px solid var(--vimiium-border);
    box-shadow: var(--vimiium-shadow);
    border-radius: 6px;
}

div.vimiumHUD span#hud-find-input,
div.vimiumHUD .vimiumHUDSearchAreaInner {
    color: var(--vimiium-fg);
    font-family: var(--vimiium-font);
    font-size: 13;
    font-weight: var(--vimiium-font-weight-medium);
}

div.vimiumHUD .hud-find {
    background-color: transparent;
    border: none;
}

div.vimiumHUD .vimiumHUDSearchArea {
    background-color: transparent;
    padding: 6px;
}
